mapper.select:
	    SELECT * FROM (
	    	SELECT T1.*, ROWNUM rnum FROM (

    		) T1
    		WHERE ROWNUM <= #{page} * #{perPage}
	    )
        WHERE rnum >= ((#{page} - 1) * #{perPage}) +1
        ORDER BY rnum DESC

mapper.insert:

	SELECT seq_post_id.NEXTVAL FROM dual

INSERT INTO POST  (
	POST_ID, TITLE, POST_CONTENT, BOARD_ID, USER_ID
) VALUES (
	#{postId}, #{title}, #{postContent}, 1, #{userId}
)


	SELECT reply_seq.NEXTVAL FROM dual

INSERT INTO reply(
	REPLY_ID, REPLY_CONTENT, REPLY_CLASS, REPLY_GROUP, REPLY_ORDER, USER_ID, POST_ID, PARENT_REPLY_ID, REG_DATE
)VALUES(
	#{replyId}, #{replyContent}, #{replyClass, jdbcType=INTEGER}, #{replyGroup, jdbcType=INTEGER}, #{replyOrder, jdbcType=INTEGER}, #{userId}, #{postId}, #{parentReplyId}, SYSDATE
)

mapper.update:
UPDATE reply
SET reply_order = reply_order + 1
WHERE reply_group = #{replyGroup} AND reply_order > #{fromOrder}
